const canvas = document.getElementById('map');
const ctx = canvas.getContext('2d');
canvas.width = window.innerWidth;
canvas.height = 800;

let map =[];
for(let i = 0; i < 3; i++){
    let new_x = getRandomInt(canvas.width);
    let new_y = getRandomInt(canvas.height);
    let el = {x:new_x,y:new_y, xx:new_x, yy:new_y}
    let elImg = new Image();
 map.push({el,elImg});
}



let isMouseDown = false;
let MdX = 0;
let MdY = 0;

// let island = {x: 0, y: 0}; 
function getRandomInt(max) {
    return Math.floor(Math.random() * Math.floor(max));
  }
canvas.addEventListener("mousedown", e=>{
     isMouseDown = true;
     map.forEach(element =>{
        let offsetX = e.offsetX-element.el.x;
        let offsetY = e.offsetY-element.el.y;

        element.el.xx = offsetX;
        element.el.yy = offsetY;

     })
});

canvas.addEventListener("mouseup", e=>{
    isMouseDown = false;              
});
canvas.addEventListener("mouseout", e=>{
    isMouseDown = false;              
});


canvas.addEventListener("mousemove", e=>{  

    let x = e.offsetX;
    let y = e.offsetY;

    if(isMouseDown){
        draw();
        map.forEach(element=> {
            console.debug(element.el.xx +" "+ element.el.yy)
            element.el.x = (element.el.xx-x)*-1;
            element.el.y = (element.el.yy-y)*-1;
        })
    }
});
function seaBG(){
    var sea = new Image();
    sea.src = "img/sea_small.jpg";

    sea.onload = function (){
        // var pattern = ctx.createPattern(sea,'repeat');
        ctx.fillStyle = "rgb(30, 181, 200)"; 
        ctx.fillRect(0,0,canvas.width,1500);

    }   
}

function draw(){
    //ctx.
    // ctx.fillRect(0,0,canvas.width-100,1000)
    seaBG();
    drawIsland();

}

function drawIsland(){

 map.forEach(island =>{
        island.elImg.src = "img/volcano.png";
        island.elImg.onload = function (){
            ctx.drawImage(island.elImg,island.el.x, island.el.y);
        };
    });
}

draw();
